package ArrayList;

public class Point
{
	int x;
	int y;

	/**
	 * 
	 * @param x
	 *            coordinate of x
	 * @param y
	 *            coordinate of y
	 */
	public Point(int x, int y)
	{
		// TODO Auto-generated constructor stub
		this.x = x;
		this.y = y;
	}

	/**
	 * @return return coordinate of asix of x and y
	 */
	public String toString()
	{
		// TODO Auto-generated method stub
		return "x = " + this.x + ", y = " + this.y;
	}
}